class Solution(object):
    def maxValue(self, n, x):
        """
        :type n: str
        :type x: int
        :rtype: str
        """
        if int(n) > 0:
            for i in range(len(n)):
                if n[i] <= str(x):
                    return n[0:i] + str(x) + n[i:]
                if i == len(n) - 1:
                    return n + str(x)
        else:
            tmp = n[1:]
            for i in range(len(tmp)):
                if i == 0:
                    if tmp[i] >= str(x):
                        return '-' + str(x) + tmp
                if i == len(tmp) - 1:
                    if tmp[i] >= str(x):
                        return "-" + tmp[0:i] + str(x) + tmp[i:]
                    else:
                        return n + str(x)
                if tmp[i] > str(x):
                    return "-" + tmp[0:i] + str(x) + tmp[i:]



n = "-"
x = 6
ss = Solution()
print(ss.maxValue(n, x))
print("-")